import './App.css';
import React, { useState } from 'react'
import { id_module } from './components/Navbar.js'
import Mark from "mark.js"
// import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
function App() {
  const [mode, setMode] = useState('light'); // Whether dark mode is enable or not
  const toggleMode = () => {
    if (mode === 'light'){
      setMode('dark');
      document.body.style.backgroundColor = '#121212';
      document.body.style.color = 'indigo-600';
    }
    else{
      setMode('light');
      document.body.style.backgroundColor = 'white';
      document.body.style.color = 'black';
    }
  }

    const handleSearch = () => {
        var ob = new Mark(document.querySelector("#myBox"));

        ob.unmark();

        // console.log(id_module.value);

        ob.mark(
          document.getElementById("text-search").value,
            { "className": "form-control" }
        );
    }
  return (
    <>
      <Navbar title="TextUtils" aboutText="About Us" mode={mode} toggleMode={toggleMode} handleSearch={handleSearch}/>
      <div className="container my-3">
        <TextForm heading="Enter the text to analyze below" mode={mode} />
        {/* <About/> */}
      </div>
    </>
  );
}

export default App;
