import React, { useState } from 'react'


export default function TextForm(props) {
    const handleUpClick = () => {
        console.log("Uppercase was clicked");
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handlelowerClick = () => {
        console.log("Lowercase was clicked");
        let newText = text.toLowerCase();
        setText(newText);
    }

    const handleOnChange = (event) => {
        console.log("On change was clicked");
        setText(event.target.value);
    }

    function countWords(str) {
        str = str.replace(/(^\s*)|(\s*$)/gi, "");
        str = str.replace(/[ ]{2,}/gi, " ");
        str = str.replace(/\n /, "\n");
        return str.split(' ').length;
    }

    const [text, setText] = useState('', 'Enter text here');
    return (
        <>
            <div className="container">
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" value={text} placeholder="Enter text here" onChange={handleOnChange} id="myBox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Convert to UpperCase</button>
                <button className="btn btn-primary mx-3" onClick={handlelowerClick}>Convert to LowerCase</button>
            </div>
            <div className="container my-5">
                <h1>Your text summary</h1>
                <p>{text === "" ?0 : countWords(text)} words, {text.length} characters</p>
                <p>{(0.008 * text.split(" ").length).toPrecision(3)} Minutes to read</p>
            </div>
        </>
    )
}
