import React, { useState } from 'react'


export default function TextForm(props) {
    const handleUpClick = () => {
        console.log("Uppercase was clicked");
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handlelowerClick = () => {
        console.log("Lowercase was clicked");
        let newText = text.toLowerCase();
        setText(newText);
    }

    const handleClear = () => {
        console.log("Clear was clicked");
        let newText = "";
        setText(newText);
    }

    const handleOnChange = (event) => {
        console.log("On change was clicked");
        setText(event.target.value);
    }

    const handleCopy = () => {
        console.log("Copied");
        var text = document.getElementById("myBox");
        text.select();
        navigator.clipboard.writeText(text.value);
    }

    const handleExtraSpaces = () => {
        let newText = text.split(/[ ]+/);
        setText(newText.join(" "))
    }

    function countWords(str) {
        str = str.replace(/(^\s*)|(\s*$)/gi, "");
        str = str.replace(/[ ]{2,}/gi, " ");
        str = str.replace(/\n /, "\n");
        return str.split(' ').length;
    }

    const [text, setText] = useState('', 'Enter text here');
    return (
        <>
            <div className="container">
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" value={text} placeholder="Enter text here" onChange={handleOnChange} id="myBox" rows="12"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Convert to UpperCase</button>
                <button className="btn btn-primary mx-2" onClick={handlelowerClick}>Convert to LowerCase</button>
                <button className="btn btn-primary mx-2" onClick={handleExtraSpaces}>Remove Extra Spaces</button>
                <button className="btn btn-danger mx-3" onClick={handleClear}>Clear text</button>
                <button className="btn btn-outline-dark mx-3" onClick={handleCopy}>Copy</button>
            </div>
            <div className="container my-5">
                <h2>Your text summary</h2>
                <p>{text === "" ? 0 : countWords(text)} words, {text.length} characters</p>
                <h3>Time: </h3>
                <p>{(0.008 * text.split(" ").length).toPrecision(3)} Minutes read</p>
                <h2>Preview: </h2>
                <p>{text}</p>
            </div>
        </>
    )
}
